DEBUG = true;
var pl = require('../lib');
var Vec2 = pl.Vec2;

describe('Debug', function() {
  it('Debug', function() {

    var world = new pl.World({
      gravity : Vec2(0, -10),
      velocityIterations : 8,
      positionIterations : 3
    });

    // Put code to debug here:

    var ground = world.createBody();
    ground.createFixture(pl.Edge(Vec2(-40.0, 0.0), Vec2(40.0, 0.0)));

    var bottom = world.createDynamicBody(Vec2(0, 0.5));
    bottom.createFixture(pl.Box(0.5, 0.5), {density : 1.0, friction : 1});

    topbox = world.createDynamicBody(Vec2(0, 1.5));
    topbox.createFixture(pl.Box(0.5, 0.5), {density : 1000.0, friction : 1});

    ground.name = 'ground';
    topbox.name = 'top   ';
    bottom.name = 'bottom';

    for (var i = 0; i < 2; i++) {
      console.log("=======================================", i);
      world.step(0.02);
      console.log(topbox.getAngle());
      console.log();
      console.log(i);
      // if (topbox.getAngle() !== 0 ) break;
    }
  });
});
